(367, '_site_transient_update_plugins', 'O:8:\"stdClass\":5:{s:12:\"last_checked\";i:1741490604;s:8:\"response\";a:2:{s:19:\"akismet/akismet.php\";O:8:\"stdClass\":13:{s:2:\"id\";s:21:\"w.org/plugins/akismet\";s:4:\"slug\";s:7:\"akismet\";s:6:\"plugin\";s:19:\"akismet/akismet.php\";s:11:\"new_version\";s:5:\"5.3.7\";s:3:\"url\";s:38:\"https://wordpress.org/plugins/akismet/\";s:7:\"package\";s:56:\"https://downloads.wordpress.org/plugin/akismet.5.3.7.zip\";s:5:\"icons\";a:2:{s:2:\"2x\";s:60:\"https://ps.w.org/akismet/assets/icon-256x256.png?rev=2818463\";s:2:\"1x\";s:60:\"https://ps.w.org/akismet/assets/icon-128x128.png?rev=2818463\";}s:7:\"banners\";a:2:{s:2:\"2x\";s:63:\"https://ps.w.org/akismet/assets/banner-1544x500.png?rev=2900731\";s:2:\"1x\";s:62:\"https://ps.w.org/akismet/assets/banner-772x250.png?rev=2900731\";}s:11:\"banners_rtl\";a:0:{}s:8:\"requires\";s:3:\"5.8\";s:6:\"tested\";s:5:\"6.7.2\";s:12:\"requires_php\";s:6:\"5.6.20\";s:16:\"requires_plugins\";a:0:{}}s:15:\"tutor/tutor.php\";O:8:\"stdClass\":13:{s:2:\"id\";s:19:\"w.org/plugins/tutor\";s:4:\"slug\";s:5:\"tutor\";s:6:\"plugin\";s:15:\"tutor/tutor.php\";s:11:\"new_version\";s:5:\"3.3.1\";s:3:\"url\";s:36:\"https://wordpress.org/plugins/tutor/\";s:7:\"package\";s:54:\"https://downloads.wordpress.org/plugin/tutor.3.3.1.zip\";s:5:\"icons\";a:2:{s:2:\"2x\";s:58:\"https://ps.w.org/tutor/assets/icon-256x256.gif?rev=3193875\";s:2:\"1x\";s:58:\"https://ps.w.org/tutor/assets/icon-128x128.gif?rev=3193875\";}s:7:\"banners\";a:2:{s:2:\"2x\";s:61:\"https://ps.w.org/tutor/assets/banner-1544x500.jpg?rev=3192898\";s:2:\"1x\";s:60:\"https://ps.w.org/tutor/assets/banner-772x250.jpg?rev=3192898\";}s:11:\"banners_rtl\";a:0:{}s:8:\"requires\";s:3:\"5.3\";s:6:\"tested\";s:5:\"6.7.2\";s:12:\"requires_php\";s:3:\"7.4\";s:16:\"requires_plugins\";a:0:{}}}s:12:\"translations\";a:0:{}s:9:\"no_update\";a:1:{s:9:\"hello.php\";O:8:\"stdClass\":10:{s:2:\"id\";s:25:\"w.org/plugins/hello-dolly\";s:4:\"slug\";s:11:\"hello-dolly\";s: